import { INIT } from "@ngrx/store";
export const UPDATE_THEME = 'UPDATE_THEME';
export class UpdateTheme {
  updatedTheme;
  type = UPDATE_THEME;
  constructor(updatedTheme) {
    this.updatedTheme = updatedTheme;
  }
}
// eslint-disable-next-line @typescript-eslint/no-unused-vars
export const THEMES = [{
  name: 'angular-app-light-theme',
  displayName: 'Light',
  isDark: false
}, {
  name: 'angular-app-dark-theme',
  displayName: 'Dark',
  isDark: true
}];
export function themeReducer(state = THEMES[0], action) {
  switch (action.type) {
    case UPDATE_THEME:
      return action.updatedTheme;
    case INIT:
      return THEMES[0];
    default:
      return state;
  }
}
