import { ADD_PROJECT, REMOVE_PROJECT, UPDATE_PROJECT } from "../actions/app-action";
import { INIT } from "@ngrx/store";
import { optionsCreationDate, optionsUpdatingDate, TKIND } from "@Global/model";
const initialState = [{
  name: "initial Project",
  version: {
    id: "id1",
    text: "description"
  },
  id: "708ef294-449d-4cf8-bead-d6c44d83ae51",
  creationDate: new Date().toLocaleString('EN', optionsCreationDate),
  updatingDate: new Date().toLocaleString('EN', optionsUpdatingDate),
  kind: TKIND.PROJECT,
  deletingDate: null
}];
export function projectReducer(state = initialState, action) {
  switch (action.type) {
    case ADD_PROJECT:
      const newProject = {
        ...action.projectToBeAdded
      };
      return [...state, newProject];
    case UPDATE_PROJECT:
      const updatedState = state.map(item => {
        if (item.id == action.id) {
          return Object.assign({}, item, {
            ...action.updatedProject
          });
        }
        return item;
      });
      return [...updatedState];
    case REMOVE_PROJECT:
      const newState = [...state];
      newState.splice(action.indexOfProjectToBeRemoved, 1);
      return newState;
    case INIT:
      return initialState;
    default:
      return state;
  }
}
